import React from "react";
import { useEffect, useState } from "react";
import { getUserProfile, getUserPosts } from "../../api"; // Assume these API functions are defined
import api from "../../api";

const ProfilePage = () => {
  const [user, setUser] = useState(null);
  const [posts, setPosts] = useState([]);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const userId = localStorage.getItem("userId");
        if (!userId) {
          setUser(null);
          setPosts([]);
          return;
        }
        const userProfile = await getUserProfile(userId);
        let userPosts = [];
        try {
          userPosts = await getUserPosts();
          // If the backend returns { message: ... } on error, set to []
          if (!Array.isArray(userPosts)) userPosts = [];
        } catch {
          userPosts = [];
        }
        setUser(userProfile);
        setPosts(userPosts);
      } catch (err) {
        setUser(null);
        setPosts([]);
        console.error("Failed to load profile:", err);
      }
    };

    fetchData();
  }, []);
  if (!user) {
    return <div>Loading...</div>; // Loading state
  }

  return (
    <div className="profile-page">
      <h1>{user.username ? user.username : "User"}'s Profile</h1>
      <img
        src={user.profilePicture ? user.profilePicture : "/cloud.png"}
        alt={`${user.username ? user.username : "User"}'s profile`}
        style={{ width: "120px", borderRadius: "50%" }}
      />
      <p>{user.bio || "No bio yet."}</p>
      <h2>Your Posts</h2>
      <ul>
        {posts.map((post) => (
          <li key={post._id || post.id}>
            <h3>{post.title}</h3>
            <p>{post.content}</p>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProfilePage;
